schemas:
DEFINE TABLE artifacts SCHEMALESS;

DEFINE FIELD object_key ON artifacts TYPE string;

DEFINE TABLE created_mother_tongue SCHEMAFULL;

DEFINE FIELD in ON created_mother_tongue TYPE record<users>;
DEFINE FIELD out ON created_mother_tongue TYPE record<mother_tongues>;
DEFINE FIELD at ON created_mother_tongue TYPE datetime;

-- Make sure that each mother_tongues can be created only once
DEFINE INDEX unique_mother_tongue_creator ON TABLE created_mother_tongue COLUMNS out UNIQUE;

DEFINE TABLE mother_tongues SCHEMALESS;

DEFINE FIELD name ON mother_tongues TYPE string;
DEFINE FIELD description ON mother_tongues TYPE string;
DEFINE FIELD is_vetted ON mother_tongues TYPE bool;

-- Define an analyzer that separates queries on spaces and lowercases them
DEFINE ANALYZER space_analyzer TOKENIZERS class FILTERS edgengram(1,15),lowercase;

-- Defining two full-text indexes on the 'name' and 'description' fields
DEFINE INDEX mother_tongues_name ON mother_tongues FIELDS name SEARCH ANALYZER space_analyzer BM25;
DEFINE INDEX mother_tongues_description ON mother_tongues FIELDS description SEARCH ANALYZER space_analyzer BM25;

DEFINE TABLE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD script_name ON script_migration TYPE string;
DEFINE FIELD executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE TABLE users SCHEMALESS;

DEFINE FIELD name ON users TYPE string;
DEFINE FIELD email ON users TYPE string;
DEFINE FIELD pw_hash ON users TYPE string;
DEFINE FIELD is_active ON users TYPE bool;
DEFINE FIELD registered_at ON users TYPE datetime;
